import {useState, useEffect, useCallback} from 'react'
import axios from 'axios'

import useLocalStorage from 'hooks/useLocalStorage'

export default url => {
  const baseUrl = 'https://api.realworld.io/api'
  const [isLoading, setIsLoading] = useState(false)
  const [response, setResponse] = useState(null)
  const [error, setError] = useState(null)
  const [options, setOptions] = useState({})
  const [token] = useLocalStorage('token') // geting saved token from Local storage
  // console.log('token', token)

  //the way how we can fetch our seted options:
  //The useÐ¡allback caches our function and react will always use the same instance of our function. The function will never change
  const doFetch = useCallback((options = {}) => {
    setOptions(options)
    setIsLoading(true)
  }, [])

  useEffect(() => {
    //added token to the headers
    const requestOptions = {
      ...options,
      ...{
        headers: {
          autorization: token ? `Token ${token}`:''
        }
      }
    }
    
    if (!isLoading) {
      return
    }
    axios(baseUrl + url, requestOptions)
    //success response, we call our response with setResponse()
      .then(response => { 
        setResponse(response.data)
        setIsLoading(false)
      })
    //error response, we call our error with setError()
      .catch(error => {
        setError(error.response.data)
        setIsLoading(false)
      })
  }, [isLoading, url, options, token])

  return [{isLoading, response, error}, doFetch]
}
